import React from 'react';
import './styles.css'; // CSS-Import für Styling

const Home = () => {
    const content = {
        title: "Willkommen bei DentaView",
        subtitle: "Ihre interaktive Plattform für Zahnanatomie und Zahngesundheit",
        description: "Tauchen Sie ein in die faszinierende Welt der Zahnanatomie. Entdecken Sie interaktive 3D-Modelle, wertvolle Tipps zur Zahnpflege und spannende Artikel rund um die Zahngesundheit. Unsere Plattform bietet Ihnen nicht nur Wissenswertes, sondern auch eine ganz neue, interaktive Lernmöglichkeit, um Ihr Wissen über die menschliche Zahngesundheit zu vertiefen.",
        features: [
            { title: "Interaktive 3D-Modelle", text: "Erforschen Sie Zähne in 3D und lernen Sie die Anatomie auf neue Weise kennen. Schauen Sie sich jedes Detail genau an, um Ihr Verständnis zu erweitern." },
            { title: "Tipps zur Zahnpflege", text: "Praktische Tipps für gesunde Zähne und ein strahlendes Lächeln. Von der richtigen Technik bis hin zu speziellen Pflegeprodukten." },
            { title: "Expertenwissen", text: "Lesen Sie Artikel von Experten über Zahngesundheit. Unsere Experten bieten fundierte Informationen, um Ihr Zahngesundheit zu fördern." },
        ],
        quicklinks: [
            { text: "Zahnanatomie", href: "/Beitrag1" },
            { text: "Tipps zur Zahnpflege", href: "/Beitrag2" },
            { text: "3D-Modell", href: "/model" },
        ],
    };

    return (
        <main>
            <header className="home-header">
                <div className="logo-title">
                    <h1>{content.title}</h1>
                    <img src="logo.png" alt="DentaView Logo" className="logo" />
                </div>
                <div className="dropdown">
                    <select className="dropdown-menu">
                        {content.quicklinks.map((link, index) => (
                            <option key={index} value={link.href}>
                                {link.text}
                            </option>
                        ))}
                    </select>
                </div>
            </header>

            <section className="hero">
                <h2>{content.subtitle}</h2>
                <p>{content.description}</p>
            </section>

            <section className="main-content">
                <h2>Warum DentaView?</h2>
                <p>{content.description}</p>
                <div className="features">
                    {content.features.map((feature, index) => (
                        <div key={index} className="feature">
                            <h3>{feature.title}</h3>
                            <p>{feature.text}</p>
                        </div>
                    ))}
                </div>
            </section>
        </main>
    );
};

export default Home;
